import { eventWrapper } from '@testing-library/user-event/dist/utils'
import { useState } from 'react'
import PersonList from './component/PersonList'
import PersonsForm from './component/PersonsForm'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [showPersons, setShowPersons] = useState(persons)

  const filterName = (event) => {
    const newShowPerson = persons.filter(person =>person.name.includes(event.target.value))
    setShowPersons(newShowPerson)
  }

  const updatePersonList = (props) => {
    const newList = persons.concat(props.new)
    setPersons(newList)
    console.log(newList)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        filter show with <input onChange={filterName}></input>
      </div>
      <h3>add a new:</h3>
      <PersonsForm persons={persons} updatePersonList={updatePersonList}/>
      <h2>Numbers</h2>
      {showPersons.map(person => <PersonList key={person.name} person={person}/>)}
    </div>
  )
}

export default App
