import { useState } from 'react'

const Display = props => {
  return (
    <div>
      {props.good.value}
      {props.neutral.value}
      {props.bad.value}
    </div>
    )
}

const Button = (props) =>{
  return(
    <button onClick={props.handleClick}>
      {props.text}
    </button>
  )
}

export default function App() {
  const [value, setValue] = useState({
    good:0,
    neutral:0,
    bad:0
  })

  const goodFeedback = () => {
    const newValue = {
      ...value,
      good:value.good+1
    }
    setValue(newValue)
  }
  const neutralFeedback = () => {
    const newValue = {
      ...value,
      neutral:value.neutral+1
    }
    setValue(newValue)
  }
  const badFeedback = () => {
    const newValue = {
      ...value,
      bad:value.bad+1
    }
    setValue(newValue)
  }
  return (
    <div>
      <h1>get feedback</h1>
      <Button handleClick={goodFeedback} text='good'/>
      <Button handleClick={neutralFeedback} text='neutral'/>
      <Button handleClick={badFeedback} text='bad'/>

      <h1>statistics</h1>
      <Display value={value}/>
    </div>
  )
}
