import React , { useState,useCallback} from 'react'

export default function PersonsForm({persons,setPersons}) {

    const [newName, setNewName] = useState('')
    const [newNumber, setNewNumber] = useState('')

    const updateList = (event) => {
        event.preventDefault()
        const newInput = {
          name: newName,
          number: newNumber,
        }
        console.log(newInput)
        if(persons.find((item) => item.name === newName)){
          window.alert(`${newName} is already added to phonebook`)
          return
        }
        if(newInput.number === ''){
            console.log(newInput.number)
          window.alert('number could not be empty')
          return
        }
        const newList = persons.concat(newInput)
        callback(newList)
      }

    const callback = useCallback((item) => setPersons(item),[setPersons])

    const nameChanged = (event) => {
        setNewName(event.target.value)
    }

    const numberChanged = (event) => {
        setNewNumber(event.target.value)
    }

    return (
    <div>
        <form onSubmit={updateList}>
        <div>
          name: <input value={newName} onChange={nameChanged}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={numberChanged}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
    </div>
   )
}
