import { eventWrapper } from '@testing-library/user-event/dist/utils'
import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ])
  const [newName, setNewName] = useState('')

  const updateList = (event) => {
    event.preventDefault()
    const newInput = {
      name:newName
    }
    if(persons.find((item) => item === newInput)){
      window.alert("same name is not allowed!")
      return
    }
    const newList = persons.concat(newInput)
    setPersons(newList)
  }
  const inputChanged = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const PersonList = ({person}) => <li>{person.name}</li>
  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={updateList}>
        <div>
          name: <input value={newName} onChange={inputChanged}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {persons.map(person => <PersonList key={person.name} person={person}/>)}
    </div>
  )
}

export default App
