import { Component } from "react";
const Course = ({course}) => {
  const total = course.parts.reduce((preValue,currentValue) => preValue+currentValue.exercises,0);
  return(
    <>
    <h3>{course.name}</h3>
    {course.parts.map(c => <li key={c.id}>{c.name} {c.exercises}</li>)}
    <h5>Total of {total} excercises</h5>
    </>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    },
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return(
    <>
    {courses.map(course=><Course key={course.id} course={course} />)}
    </>
  )
}

export default App
