import { useState } from 'react'

const Statistic = ({text,value}) => {
  return (
    <>
    <h4>{text}: {value}</h4>
    </>
  )
}

const Display = props => {
  if(props.value.good===0 && props.value.neutral===0 && props.value.bad===0){
    return (
      <>
      <h4>No feedback given</h4>
      </>
    )
  }
  // console.log(props.value.good)
  const all = props.value.good+props.value.neutral+props.value.bad
  const average = (props.value.good-props.value.bad)/all
  const positive = (props.value.good/all)*100+'%'
  return (
    <div>
      <Statistic text='good' value={props.value.good}/>
      <Statistic text='neutral' value={props.value.neutral}/>
      <Statistic text='bad' value={props.value.bad}/>
      <Statistic text='ALL' value={all}/>
      <Statistic text='average' value={average}/>
      <Statistic text='positive' value={positive}/>
    </div>
    )
}

const Button = (props) =>{
  return(
    <button onClick={props.handleClick}>
      {props.text}
    </button>
  )
}

export default function App() {
  const [value, setValue] = useState({
    good:0,
    neutral:0,
    bad:0
  })

  const goodFeedback = () => {
    const newValue = {
      ...value,
      good:value.good+1
    }
    setValue(newValue)
  }
  const neutralFeedback = () => {
    const newValue = {
      ...value,
      neutral:value.neutral+1
    }
    setValue(newValue)
  }
  const badFeedback = () => {
    const newValue = {
      ...value,
      bad:value.bad+1
    }
    setValue(newValue)
  }
  return (
    <div>
      <h1>get feedback</h1>
      <Button handleClick={goodFeedback} text='good'/>
      <Button handleClick={neutralFeedback} text='neutral'/>
      <Button handleClick={badFeedback} text='bad'/>

      <h1>statistics</h1>
      <Display value={value}/>
    </div>
  )
}
