import { eventWrapper } from '@testing-library/user-event/dist/utils'
import { useState } from 'react'
import PersonList from './component/PersonList'
import PersonsForm from './component/PersonsForm'

const App = () => {
  const [showPersons, setShowPersons] = useState(persons)

  const filterName = (event) => {
    const newShowPerson = persons.filter(person =>person.name.includes(event.target.value))
    setShowPersons(newShowPerson)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        filter show with <input onChange={filterName}></input>
      </div>
      <h3>add a new:</h3>
      <PersonsForm persons={persons} setPersons={setPersons}/>
      <h2>Numbers</h2>
      {showPersons.map(person => <PersonList key={person.name} person={person}/>)}
    </div>
  )
}

export default App
