const Course = ({course}) => {
  let total = 0
  for(var i=0;i<course.parts.length;i++){
    total=total+course.parts[i].exercises
  }
  return(
    <>
    <h3>{course.name}</h3>
    {course.parts.map(c => <li key={c.id}>{c.name} {c.exercises}</li>)}
    <h5>Total of {total} excercises</h5>
    </>
  )
}

const App = () => {
  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  }

  return <Course course={course} />
}

export default App
