import { useState } from 'react'

export default function App() {
  const [counter, setCounter] = useState(0)

  setTimeout(() => {
    setCounter(counter+2)

  }, 1000);

  const handleClick = () =>{
    setCounter(counter+1)
  }

  return (
    <>
    <div>{counter}</div>
    <button onClick={handleClick}>
      plus
    </button>
    <button onClick={() => setCounter(0)}>
      reset
    </button>
    </>
  )
}
