import React , {useState} from 'react'

export default function PersonsForm() {
    const [persons, setPersons] = useState([
        { name: 'Arto Hellas', number: '040-123456', id: 1 },
        { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
        { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
        { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
      ])

    const [newName, setNewName] = useState('')
    const [newNumber, setNewNumber] = useState('')

    const updateList = (event) => {
        event.preventDefault()
        const newInput = {
          name: newName,
          number: newNumber,
        }
        if(persons.find((item) => item.name === newName)){
          window.alert(`${newName} is already added to phonebook`)
          return
        }
        if(newInput.number === ''){
          window.alert('number could not be empty')
          return
        }
        const newList = persons.concat(newInput)
        setPersons(newList)
      }

    const nameChanged = (event) => {
        setNewName(event.target.value)
    }

    const numberChanged = (event) => {
        setNewNumber(event.target.value)
    }

    return (
    <div>
        <form onSubmit={updateList}>
        <div>
          name: <input value={newName} onChange={nameChanged}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={numberChanged}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
    </div>
   )
}
