const Header = (props) => {
  console.log(props)
  return (
    <>
      <h1>{props.course}</h1>
    </>
  );
}

const Content = (props) => {
  return (
    <>
      <Part part={props[0].part} excercise={props[0].excercise}/>
      <Part part={props[1].part} excercise={props[1].excercise}/>
      <Part part={props[2].part} excercise={props[2].excercise}/>
    </>
  );
}

const Part = (props) => {
  return(
    <p>
        {props.part} {props.excercise}
    </p>
  );
}

const Total = (props) => {
  return(
    <>
      <p>Number of exercises {props[0].excercise + props[1].excercise + props[2].excercise}</p>
    </>
  );
}
const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]


  return (
    <div>
      <Header course={course} />
      <Content part={parts}/>
      <Total part={parts}/>
    </div>
  )
}

export default App
